#include &lt;bits/stdc++.h&gt; #define forn(i, n) for (int i = 0; i &lt; int(n); ++i) #define pb push_back  using namespace std;  typedef long long ll; const ll INF = 1e12;  int main() {  ios::sync_with_stdio(false);  cin.tie(nullptr); 	ll x;  cin&gt;&gt;x;  vector&lt;ll&gt; vec;  for(ll f=2;f*f&lt;=x;f++){  ll tmp=1;  while(x%f==0){  tmp*=f;  x/=f;  }  if(tmp!=1) vec.pb(tmp);  }  if(x&gt;1)vec.pb(x);  ll aa=INF,ab=INF;  for(ll i=0;i&lt;(1&lt;&lt;vec.size());i++){  ll a=1,b=1;  forn(j,vec.size()){  if((i&amp;(1&lt;&lt;j))&gt;0) a*=vec[j];  else b*=vec[j];  }  if(max(a,b)&lt;max(aa,ab)){  aa=a;  ab=b;  }  }  cout&lt;&lt;ab&lt;&lt;&#39; &#39;&lt;&lt;aa;  return 0; } Looping factors:
