&#39;;} int main() { ios::sync_with_stdio(false); cin.tie(nullptr); vector a(9, vector(9, 0)); for (auto&amp; v: a) for (auto&amp; i : v) cin&gt;&gt;i; auto check_row=[&amp;](int row, auto&amp; cnt, int x) { for (int i=0; i&lt;9; i++) { cnt[row][i][x]=0; } }; auto check_col=[&amp;](int col, auto&amp; cnt, int x) { for (int i=0; i&lt;9; i++) { cnt[i][col][x]=0; } }; auto check_grid=[&amp;](int r, int c, auto&amp; cnt, int x) { int num=r/3*3+c/3; r=num/3*3, c=num%3*3; for (int i=r; i&lt;r+3; i++) { for (int j=c; j&lt;c+3; j++) { cnt[i][j][x]=0; } } }; auto count_row=[&amp;](int row, auto&amp; cnt, int x) { int c=0; for (int i=0; i&lt;9; i++) { if (a[row][i]) continue; c+=cnt[row][i][x]; } return c; }; auto count_col=[&amp;](int col, auto&amp; cnt, int x) { int c=0; for (int i=0; i&lt;9; i++) { if (a[i][col]) continue; c+=cnt[i][col][x]; } return c; }; auto count_grid=[&amp;](int r, int c, auto&amp; cnt, int x) { int num=r/3*3+c/3; r=num/3*3, c=num%3*3; int cc=0; for (int i=r; i&lt;r+3; i++) { for (int j=c; j&lt;c+3; j++) { if (a[i][j]) continue; cc+=cnt[i][j][x]; } } return cc; }; while (true) { int found=0; vector cnt(9, vector(9, vector(10, 1))); for (int i=0; i&lt;9; i++) { for (int j=0; j&lt;9; j++) { cnt[i][j][0]=0; if (a[i][j]!=0) { check_col(j, cnt, a[i][j]); check_row(i, cnt, a[i][j]); check_grid(i, j, cnt, a[i][j]); } } } for (int i=0; i&lt;9; i++) { for (int j=0; j&lt;9; j++) { if (a[i][j]==0) { if (count(all(cnt[i][j]), 1)==1) { found=1; auto it=find(all(cnt[i][j]), 1); a[i][j]=it-cnt[i][j].begin(); goto next; } for (int v=1; v&lt;=9; v++) { if ((count_col(j, cnt, v)==1 || count_row(i, cnt, v)==1 || count_grid(i, j, cnt, v)==1) &amp;&amp; cnt[i][j][v]) { found=1; a[i][j]=v; goto next; } } } } } next: if (found==0) break; } int cc=0; for (auto&amp; v : a) for (auto i : v) cc+=i==0; if (cc) { cout&lt;&lt;&#34;Not easy
&#34;; for (auto&amp; v : a) { for (auto&amp; i : v) { if (i==0) cout&lt;&lt;&#39;.&#39;; else cout&lt;&lt;i; cout&lt;&lt;&#39; &#39;; } cout&lt;&lt;&#39;
