#include &lt;bits/stdc++.h&gt; #define all(x) (x).begin(),(x).end() #define sz(x) int(x.size())  using namespace std; using ll = long long; using pii = pair&lt;int, int&gt;; template&lt;typename... T&gt; void rd(T&amp;... args) {((cin&gt;&gt;args), ...);} template&lt;typename... T&gt; void wr(T... args) {((cout&lt;&lt;args&lt;&lt;&#34; &#34;), ...); cout&lt;&lt;&#39;\n&#39;;} namespace IO { const int MAXSIZE = 1 &lt;&lt; 20; char buf[MAXSIZE], *p1, *p2; #define gc() \ (p1 == p2 &amp;&amp; (p2 = (p1 = buf) + fread(buf, 1, MAXSIZE, stdin), p1 == p2) \ ? EOF \ : *p1++) inline int rd() { int x = 0, f = 1; char c = gc(); while (!isdigit(c)) { if (c == &#39;-&#39;) f = -1; c = gc(); } while (isdigit(c)) x = x * 10 + (c ^ 48), c = gc(); return x * f; } char pbuf[1 &lt;&lt; 20], *pp = pbuf; inline void push(const char &amp;c) { if (pp - pbuf == 1 &lt;&lt; 20) fwrite(pbuf, 1, 1 &lt;&lt; 20, stdout), pp = pbuf; *pp++ = c; } inline void write(int x) { static int sta[35]; int top = 0; do { sta[top++] = x % 10, x /= 10; } while (x); while (top) push(sta[--top] + &#39;0&#39;); } } // const int N=2e6; int ans[N+1]; bool not_prime[N+1]; int main() { ios::sync_with_stdio(false); cin.tie(nullptr); int q=IO::rd(); for (int i=2; i&lt;=N; i++) { if (!not_prime[i]) { for (int j=i+i; j&lt;=N; j+=i) not_prime[j]=1; } else { for (int j=i; j&lt;=N; j+=i) { ans[j]++; } } } while (q--) { int x=IO::rd(); printf(&#34;%d\n&#34;, ans[x]+1); } return 0; } J. SG Coin 其实就是个取模下的减法。。。
L. Wi Know 首先我们观察到：对于$i&lt;j&lt;k, S_i=S_j=S_k$，$(S_i, S_k)$一定不差于$(S_j, S_k)$。所以在$A, B, A, B$ 中第一个A我们一定选在$S$中第一次出现的A。同理，第二个B一定选$S$中最后一出现的B。
解法的大致思路就是固定B找最小的A。一种比较naive的思路是在$[i+1, last_i-1]$中查询最小值，但有两个问题：
 不知道最小值在$i$之前有没有出现过。 最小值可能等于$S_i$。  所以我们不能一次把所有的数都放到线段树里，要按一定的顺序放。对于每个位置$i$，我们记录一个$nxt_i$为$S_i$的下一个出现位置。然后我们遍历$S$，首先查询$[i+1, last_i-1]$中的最小值min，然后用{min, S[i]}更新答案，最后在线段树中把$next_i$设为$S_i$。
这样为什么避免了上面的两个问题呢？首先，只有在$i$之前出现过的数才会被加进去，避免了问题1，然后我们是先查询再添加，而且一次只加一个，这样就避免问题2。总之这个解法还是很妙的，比官方题解简单不少。
#include &lt;bits/stdc++.h&gt; #define all(x) (x).begin(),(x).end() #define sz(x) int(x.size())  using namespace std; using ll = long long; using pii = pair&lt;int, int&gt;; template&lt;typename... T&gt; void rd(T&amp;... args) {((cin&gt;&gt;args), ...);} template&lt;typename... T&gt; void wr(T... args) {((cout&lt;&lt;args&lt;&lt;&#34; &#34;), ...); cout&lt;&lt;&#39;\n&#39;;} struct SegTree{ int n; vector&lt;int&gt; t; SegTree(int n_):n(n_),t(4*n, 1e9){} void pushup(int node){ t[node]=min(t[node&lt;&lt;1],t[node&lt;&lt;1|1]); } void update(int node,int i,int x,int l,int r){ if(l==r){ t[node]=x; return; } int mid=(l+r)/2; if(i&lt;=mid) update(node&lt;&lt;1,i,x,l,mid); else update(node&lt;&lt;1|1,i,x,mid+1,r); pushup(node); } void update(int i, int x) { update(1, i, x, 0, n-1); } int query(int node,int ql,int qr,int l,int r){ if (ql &gt; r || qr &lt; l) return 1e9; if(ql&lt;=l&amp;&amp;qr&gt;=r){ return t[node]; } int mid=(l+r)&gt;&gt;1; return min(query(node&lt;&lt;1,ql,qr,l,mid), query(node&lt;&lt;1|1,ql,qr,mid+1,r)); } int query(int l, int r) { return query(1, l, r, 0, n-1); } }; int main() { ios::sync_with_stdio(false); cin.tie(nullptr); int n; cin&gt;&gt;n; vector&lt;int&gt; a(n); vector&lt;int&gt; pos(n+1, -1), nxt(n, -1), last(n+1, -1); for (int i=0; i&lt;n; i++) { cin&gt;&gt;a[i]; last[a[i]]=i; } for (int i=n-1; i&gt;=0; i--) { nxt[i]=pos[a[i]]; pos[a[i]]=i; } pair&lt;int, int&gt; ans={n+1, n+1}; SegTree st(n); for (int i=0; i&lt;n; i++) { int x=st.query(i+1, last[a[i]] - 1); ans=min(ans, { x, a[i] }); st.update(nxt[i], a[i]); } if (ans.first&lt;=n) cout&lt;&lt;ans.first&lt;&lt;&#39; &#39;&lt;&lt;ans.second&lt;&lt;&#39;\n&#39;; else cout &lt;&lt; -1; return 0; }

